kickstart.post:
DROP TABLE IF EXISTS vm_nodes; CREATE TABLE vm_nodes ( ID int(11) NOT NULL auto_increment, PhysNode int(11) NOT NULL default '0', Node int(11) NOT NULL default '0', Mem int(11) NOT NULL default '1024', Slice int(11) NOT NULL default '0', Virt_Type varchar(64) default NULL, RunProfile varchar(64) default NULL, InstallProfile varchar(64) NOT NULL default 'install', PRIMARY KEY (ID) ) TYPE=MyISAM;
DROP TABLE IF EXISTS vm_disks; CREATE TABLE vm_disks ( ID int(11) NOT NULL auto_increment, Vm_Node int(11) NOT NULL default '0', VBD_Type varchar(64) default NULL, Prefix varchar(512) default NULL, Name varchar(512) default NULL, Device varchar(512) default NULL, Mode varchar(64) default NULL, Size int(11) NOT NULL default '0', PRIMARY KEY (ID) ) TYPE=MyISAM;
DROP TABLE IF EXISTS vm_profiles; CREATE TABLE vm_profiles( ID int(11) NOT NULL auto_increment, Vm_Node int(11) NOT NULL default '0', Profile varchar(512) default NULL, Kernel varchar(512) default NULL, Ramdisk varchar(512) default NULL, Args varchar(2048) default NULL, PRIMARY KEY (ID) ) TYPE=MyISAM;
